graphql.query:
mutation SignIn {
    signIn(data: { username: "docblizzard", password: "123" }) {
        code
        response
        token {
            token
        }
    }
}

mutation CreateUser {
    createUser(data: { username: "docblizzard2", password: "123" }) {
        id
        username
    }
}

mutation CreateConversation($senderUsername: String!, $receiverUsername: String!) {
        createConversation(data :{senderUsername: $senderUsername, receiverUsername: $receiverUsername}) {
        conversation {
          id
        }
        response
        code
    }
}

query GetMessageFromConv {
    getMessageFromConv(data: "1b45d732-610f-4d53-a63d-666e5d2fa2e7") {
        code
        response
        messages {
            id
            message
            createdAt
            userId
            receiverId
        }
    }
}

mutation CreateMessage {
    createMessage(
        data: {
            conversationId: "1b45d732-610f-4d53-a63d-666e5d2fa2e7"
            userId: "4f221279-57cb-4e8c-bca7-0f997a966988"
            message: "Hello, nice to meet ya"
            receiverId: "b27c11fd-88dd-4834-aa97-45440231ee01"
        }
    ) {
        code
        response
        messageSent {
            message
            createdAt
        }
    }
}

